function displayMovies(e){for(const t of e){const e=document.createElement("div");e.classList.add("film-box");const o=document.createElement("img");o.src=t.imagePath,o.alt=t.name,o.loading="lazy";const n=document.createElement("h4");n.textContent=t.name;const i=document.createElement("h6");i.textContent=t.category.join(" "),e.addEventListener("click",()=>{window.location.href=`details.html?id=${t.id}`}),e.appendChild(o),e.appendChild(n),e.appendChild(i),filmBoxMovies.appendChild(e)}}function getIdMoviesCookie(){const e=document.cookie.split("; ");for(const t of e){const[e,o]=t.split("=");if("idMovies"===e)return"[]"==o?null:decodeURIComponent(o)}return null}const filmBoxMovies=document.querySelector(".film-box-lists");document.addEventListener("DOMContentLoaded",()=>{let e=getIdMoviesCookie();console.log(null==e);const t=document.querySelector(".emptylist"),o=document.querySelector(".explore");if(null!==e){t.classList.add("hide"),o.classList.remove("hide");const n=JSON.parse(e),i=movies.filter(e=>n.includes(String(e.id)));displayMovies(i)}else t.classList.remove("hide"),o.classList.add("hide")});